mapper.select:
select 

from user
where userNo = #{userno}
and pwd = #{pwd}

select 

from user
where 1=1
   
    and userNo like #{userno}
  
  
    and userName like #{username}
  
  
    and position like #{position}

select 
	count(*) 
from user
where 1=1
   
    and userNo like #{userno}
  
  
    and userName like #{username}
  
  
    and position like #{position}
